void apply_norm(double *imdif, unsigned short *flag0, unsigned short *flag1,
                double coeff, int nx, int ny, double bad_value)
{
        int     idx;
        double  norm;

  norm = 1.0/coeff;

  for (idx=0; idx<nx*ny; idx++)
    if ((!flag0[idx]) || (!flag1[idx])) imdif[idx]  = bad_value;
    else                                imdif[idx] *= norm;

  return;
}
